package com.accuratebits.tallkingheads.navigationimport androidx.compose.foundation.layout.WindowInsetsimport androidx.compose.foundation.layout.navigationBarsimport androidx.compose.material3.*import androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.vector.ImageVectorimport androidx.compose.ui.res.vectorResourceimport androidx.compose.ui.text.style.TextAlignimport androidx.compose.ui.text.style.TextOverflowimport com.accuratebits.tallkingheads.ui.theme.Pink40import com.accuratebits.tallkingheads.ui.theme.Pink80import com.accuratebits.tallkingheads.ui.theme.Purple80@Composablefun TalkingHeadsBottomNavigation(    allScreens: List<AppScreens>,    onTabSelected: (AppScreens) -> Unit,    currentScreen: AppScreens,    modifier: Modifier = Modifier) {    NavigationBar(        modifier,        windowInsets = WindowInsets.navigationBars    ) {        allScreens.forEach { screen ->            val selected = currentScreen == screen            NavigationBarItem(                icon = {                    Icon(                        imageVector = ImageVector.vectorResource(id = screen.icon),                        contentDescription = null,                        //modifier = Modifier.height(20.dp)                    )                },                label = {                    Text(                        text = screen.title,                        maxLines = 1,                        overflow = TextOverflow.Visible,                        softWrap = false,                        textAlign = TextAlign.Center,                        color = if (selected) Purple80 else Pink80                    )                },                selected = selected,                onClick = { onTabSelected(screen) },                colors = NavigationBarItemDefaults.colors(                    indicatorColor = Purple80,                    selectedTextColor = Pink80,                    selectedIconColor = Pink80,                    unselectedTextColor = Pink40,                    unselectedIconColor = Pink40                )            )        }    }}